import React from "react";

const ListGroup = props => {
  const { items, currentItem, onItemSelect } = props;
  return (
    <div>
      <ul className="list-group">
        {items.map(i => (
          <li
            key={i._id}
            onClick={() => onItemSelect(i)}
            className={
              items.indexOf(i) === currentItem
                ? "list-group-item active"
                : "list-group-item"
            }>
            {i.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListGroup;
