import React, { Component } from "react";

class Like extends Component {
  state = {
    liked: 0
  };
  toggle = () => {
    this.setState({ liked: 0 });
  };
  likeState = () => {
    return this.state.liked ? "-o" : "";
  };
  render() {
    const likeState = this.likeState();
    return (
      <span onClick={this.toggle}>
        <i className={`fa fa-heart${likeState} aria-hidden="true"`} />
      </span>
    );
  }
}

export default Like;
