import React, { Component } from "react";
import Navbar from "./components/navbarTop";
import Movies from "./components/movies";
import * as MovieServices from "./services/fakeMovieService";
import "./App.css";
import Pagination from "./components/common/pagination";

class App extends Component {
  state = {
    movies: MovieServices.getMovies(),
    currentPage: 1
  };

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
    if (
      movies.lenght &&
      this.state.currentPage > Math.floor(movies.length / 4)
    ) {
      this.setState({ currentPage: this.state.currentPage - 1 });
    }
    console.log(
      "decrement page",
      this.state.currentPage,
      Math.floor(movies.length / 4)
    );
  };

  handleLiked = movie => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  getTotalPages = nbrMovies => {
    const moviesPerPages = 4;
    return Math.ceil(nbrMovies / moviesPerPages);
  };

  getMoviesOnPage = () => {
    const firstMovieIndex = (this.state.currentPage - 1) * 4;
    const lastMovieIndex = firstMovieIndex + 4;
    return this.state.movies.slice(firstMovieIndex, lastMovieIndex);
  };

  handlePageClicked = pageIndex => {
    this.setState({ currentPage: pageIndex });
  };

  render() {
    const nbrMovies = this.state.movies.length;
    const movies = this.getMoviesOnPage();
    return (
      <React.Fragment>
        <Navbar nbrMovies={nbrMovies} />
        <main className="container">
          {nbrMovies ? (
            <Movies
              onDelete={this.handleDelete}
              onLiked={this.handleLiked}
              movies={movies}
            />
          ) : (
            ""
          )}
          <Pagination
            currentPage={this.state.currentPage}
            totalPages={this.getTotalPages(nbrMovies)}
            onPageClicked={this.handlePageClicked}
          />
        </main>
      </React.Fragment>
    );
  }
}

export default App;
