import React, { Component } from "react";

const Like = ({ liked, onLiked }) => {
  const classes = "fa fa-heart";
  if (!liked) classes += "-o";
  return (
    <span onClick={onLiked}>
      <i className={`${classes} aria-hidden="true"`} />
    </span>
  );
};

export default Like;
