import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";
import { getMovie, getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";

class MovieForm extends Form {
  state = {
    data: { _id: "", title: "", genre: "", stock: "", rate: "" },
    _id: "",
    genres: getGenres(),
    errors: {}
  };
  componentWillMount() {
    const movies = getMovies();
    if (!movies.find(m => m._id === movieId)) {
      this.props.history.replace("/not-found");
      return;
    }
  }
  componentDidMount() {
    const movieId = this.props.match.params.id;

    if (movieId === "new") return;

    const {
      title,
      genre,
      numberInStock: stock,
      dailyRentalRate: rate
    } = getMovie(movieId);
    const data = { title, genre: genre.name, stock, rate };
    this.setState({ data, _id: movieId });
  }

  schema = {
    title: Joi.string()
      .required()
      .label("Title"),
    genre: Joi.string()
      .required()
      .label("Genre"),
    stock: Joi.number()
      .required()
      .integer()
      .min(0)
      .max(100)
      .label("Number in Stock"),
    rate: Joi.number()
      .required()
      .min(0)
      .max(10)
      .label("Daily Rental Rate")
  };

  doSubmit = () => {
    console.log("Submitted");
    this.props.history.push({
      pathname: "/movies",
      state: { data: { ...this.state.data }, _id: this.state._id || "new" }
    });
  };

  render() {
    const { data, genres, errors } = this.state;
    this.checkPath();
    return (
      <div>
        <h1>Movie Form</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          <div className="form-group">
            <label htmlFor="genreSelect">Genre</label>
            <select
              className="form-control"
              onChange={this.handleChange}
              id="genreSelect"
              name="genre"
              value={data.genre}>
              <option />
              {genres.map(genre => (
                <option key={genre._id} value={genre.name}>
                  {genre.name}
                </option>
              ))}
            </select>
            {errors["genre"] && (
              <div className="alert alert-danger">{errors["genre"]}</div>
            )}
          </div>
          {this.renderInput("stock", "Number in Stock", "number")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;

