import React from "react";
import { builtinModules } from "module";

const Like = ({ liked, onLiked }) => {
  let classes = "fa fa-heart";
  if (!liked) classes += "-o";
  return (
    <div>
      <button className="btn" />
      <span onClick={onLiked} className="btn">
        <i className={`${classes} aria-hidden="true"`} />
      </span>
    </div>
  );
};

export default Like;
