import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";
import { getMovie } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";

class MovieForm extends Form {
  state = {
    data: { title: "", genre: {}, stock: "", rate: "" },
    genres: {},
    errors: {}
  };
  componentDidMount() {
    const movieId = this.props.match.params.id;
    if (movieId === "new") return;
    const {
      title,
      genre,
      numberInStock: stock,
      dailyRentalRate: rate
    } = getMovie(movieId);
    const data = { title, genre, stock, rate };
    const genres = getGenres();
    this.setState({ data, genres });
  }

  schema = {
    title: Joi.string()
      .required()
      .label("Title"),
    stock: Joi.number()
      .required()
      .min(0)
      .max(100)
      .label("Number in Stock"),
    genre: Joi.string(),
    rate: Joi.number()
      .required()
      .min(0)
      .max(10)
      .label("Daily Rental Rate")
  };

  doSubmit = () => {
    console.log("Submitted");
  };

  handleSelectChange = ({ currentTarget: input }) => {
    const data = { ...this.state.data };
    data["genre"] = input.value;
    this.setState({ data });
  };

  render() {
    console.log(this.state.data, this.state.genres);
    return (
      <div>
        <h1>Movie Form</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          <div className="form-group">
            <label htmlFor="genreSelect">Genre</label>
            <select
              className="form-control"
              onChange={this.handleSelectChange}
              id="genreSelect">
              <option />
              {/* {this.state.genres.map(genre => (
                <option>genre.name</option>
              ))} */}
            </select>
          </div>
          {this.renderInput("stock", "Number in Stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </div>
    );
  }
}

export default MovieForm;

