import React, { Component } from "react";
import Navbar from "./components/navbarTop";
import Movies from "./components/movies";
import { getMovies } from "./services/fakeMovieService";
import "./App.css";
import Pagination from "./components/common/pagination";
import { paginate } from "./utils/paginate";
import ListGroup from "./components/common/listGroup";
import { getGenres } from "./services/fakeGenreService";

class App extends Component {
  state = {
    movies: [],
    genres: [],
    pageSize: 4,
    currentPage: 1,
    selectedGenre: {}
  };

  componentDidMount() {
    this.setState({ movies: getMovies(), genres: getGenres() });
  }
  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLiked = movie => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  getTotalPages = nbrMovies => {
    const moviesPerPages = 4;
    return Math.ceil(nbrMovies / moviesPerPages);
  };

  handlePageChange = page => {
    this.setState({ currentPage: page });
  };

  handleGenreSelect = genre => {
    this.setState({ selectedGenre: genre });
  };

  render() {
    const {
      pageSize,
      currentPage,
      movies: allMovies,
      genres,
      selectedGenre
    } = this.state;
    const filtered = selectedGenre
      ? allMovies.filter(m => m.genre._id === selectedGenre._id)
      : allMovies;
    let filteredMovies = [...allMovies];
    if (selectedGenre) {
      filteredMovies = filteredMovies.filter(
        m => m.genre._id === genres[selectedGenre]._id
      );
      console.log(filteredMovies);
    }
    const movies = paginate(filteredMovies, currentPage, pageSize);
    const nbrMovies = filteredMovies.length;
    return (
      <div className="row">
        <div className="col-3">
          <ListGroup
            items={genres}
            selectedItem={selectedGenre}
            onItemSelect={this.handleGenreSelect}
          />
        </div>
        <div className="col">
          <Navbar nbrMovies={nbrMovies} />
          <main className="container">
            {nbrMovies ? (
              <Movies
                onDelete={this.handleDelete}
                onLiked={this.handleLiked}
                movies={movies}
              />
            ) : (
              ""
            )}
            <Pagination
              itemsCount={nbrMovies}
              pageSize={pageSize}
              currentPage={currentPage}
              onPageChange={this.handlePageChange}
            />
          </main>
        </div>
      </div>
    );
  }
}

export default App;
