import React, { Component } from "react";
import MoviesTable from "./moviesTable";
import { getMovies } from "../services/fakeMovieService";
import Pagination from "./common/pagination";
import { paginate } from "../utils/paginate";
import ListGroup from "./common/listGroup";
import { getGenres } from "../services/fakeGenreService";
import _ from "lodash";
import { Link } from "react-router-dom";

class Movies extends Component {
  state = {
    movies: [],
    genres: [],
    pageSize: 4,
    currentPage: 1,
    sortColumn: { path: "title", order: "asc" }
  };

  componentWillMount() {
    const genres = [{ _id: "", name: "All Genre" }, ...getGenres()];
    const movies = getMovies();
    const { state: locState } = this.props.location;
    if (locState) {
      const { movie } = locState;
      if (movie._id === "new") this.addMovie(movies, movie);
      else {
        this.updateMovie(movies, movie);
      }
    } else this.setState({ movies });
    this.setState({ genres, selectedGenre: genres[0] });
  }

  updateMovie = (movies, movieNew) => {
    const { title, genre, stock, rate, _id } = movieNew;
    const movie = movies.find(m => m._id === movieNew._id);
    const index = movies.indexOf(movie);
    movies[index] = {
      title,
      genre,
      numberInStock: stock,
      dailyRentalRate: rate,
      _id
    };
    this.setState({ movies });
  };

  addMovie = (movies, movieNew) => {
    console.log("add movie", movieNew);
    const { title, genre, stock, rate, _id } = movieNew;
    const movie = {
      liked: 0,
      genre,
      _id,
      title,
      dailyRentalRate: rate,
      numberInStock: stock
    };
    console.log("movie to add", movie, genre);
    movies[9] = movie;
    console.log("movies", movies);
    this.setState({ movies });
  };

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLiked = movie => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  handlePageChange = page => {
    this.setState({ currentPage: page });
  };

  handleGenreSelect = genre => {
    this.setState({ selectedGenre: genre, currentPage: 1 });
  };

  handleSort = sortColumn => {
    this.setState({ sortColumn });
  };

  getPagedData = () => {
    const {
      pageSize,
      currentPage,
      movies: allMovies,
      selectedGenre,
      sortColumn
    } = this.state;
    const filtered =
      selectedGenre && selectedGenre._id
        ? allMovies.filter(m => m.genre._id === selectedGenre._id)
        : allMovies;
    const sorted = _.orderBy(filtered, [sortColumn.path], [sortColumn.order]);
    const movies = paginate(sorted, currentPage, pageSize);
    return { totalCount: filtered.length, data: movies };
  };

  render() {
    const { length: count } = this.state.movies;
    const {
      pageSize,
      currentPage,
      genres,
      selectedGenre,
      sortColumn
    } = this.state;

    if (!count) return <h3>No movie in database</h3>;
    const { totalCount, data: movies } = this.getPagedData();
    return (
      <div className="row">
        <div className="col-3">
          <ListGroup
            items={genres}
            selectedItem={selectedGenre}
            onItemSelect={this.handleGenreSelect}
          />
        </div>
        <div className="col">
          <Link
            to="/movie/new"
            className="btn btn-primary"
            style={{ marginBottom: 20 }}>
            New Movie
          </Link>
          <h3>Showing {totalCount} movies in the database</h3>
          <main className="container">
            <MoviesTable
              onDelete={this.handleDelete}
              onLiked={this.handleLiked}
              onSort={this.handleSort}
              sortColumn={sortColumn}
              movies={movies}
            />
            <Pagination
              itemsCount={totalCount}
              pageSize={pageSize}
              currentPage={currentPage}
              onPageChange={this.handlePageChange}
            />
          </main>
        </div>
      </div>
    );
  }
}

export default Movies;
