import React, { Component } from "react";

class Like extends Component {
  state = {
    liked: 0
  };
  handleClick = () => {
    this.setState({ liked: !this.state.liked });
  };

  render() {
    return (
      <span onClick={this.handleClick()}>
        <i className={this.likeState()} aria-hidden="true" />
      </span>
    );
  }
}

export default Like;
